  find_pos = strstr(chlg, key);
  if(!find_pos)
    return FALSE;

  find_pos = strlen(key);

  for(i = 0; *find_pos && *find_pos != end_char && i < max_val_len - 1; �)
    value[i] = *find_pos�;
  value[i] = '\0';

  return TRUE;
